ller@destroy')->middleware('auth:api');        Route::post('shops/{id}/verify', 'ShopController@verify')->middleware('auth:api');        Route::put('shops/batch', 'ShopController@batchUpdate')->middleware('auth:api');        Route::get('freight-templates', 'FreightTemplateController@index');        Route::get('freight-templates/{id}', 'FreightTemplateController@show');        Route::post('freight-templates', 'FreightTemplateController@store')->middleware('auth:api');        Route::put('freight-templates/{id}', 'FreightTemplateController@store')->middleware('auth:api');        Route::delete('freight-templates/{id}', 'FreightTemplateController@destroy')->middleware('auth:api');        Route::get('shipping-zones', 'ShippingZoneController@index');        Route::get('shipping-zones/{id}', 'ShippingZoneController@show');        Route::post('shipping-zones', 'ShippingZoneController@store')->middleware('auth:api');        Route::put('shipping-zones/{id}', 'ShippingZoneController@store')->middleware('auth:api');        Route::delete('shipping-zones/batch', 'ShippingZoneController@batchDestroy')->middleware('auth:api');        Route::get('orders', 'OrderController@index')->middleware('auth:api');        Route::get('orders/{id}', 'OrderController@show')->middleware('auth:api');        Route::post('orders', 'OrderController@store')->middleware('auth:api');        //照片墙        Route::get('photo-walls', 'PhotoWallController@index');        Route::post('photo-walls', 'PhotoWallController@store')->middleware('auth:api');        Route::post('photo-walls/batch', 'PhotoWallController@batchStore')->middleware('auth:api');        Route::delete('photo-walls/batch', 'PhotoWallController@batchDestroy')->middleware('auth:api');        Route::delete('photo-walls/{id}', 'PhotoWallController@destroy')->middleware('auth:api');        //配送员        Route::get('deliveryers', 'DeliveryerController@index');        Route::get('deliveryers/{id}', 'DeliveryerController@show')