e::get('settings', 'SettingController@index');        Route::post('settings', 'SettingController@update');        //素材管理        Route::get('materials', 'MaterialController@index');        Route::get('materials/types', 'MaterialController@types');        Route::get('materials/{id}', 'MaterialController@show');        Route::put('materials/{id}', 'MaterialController@update');        Route::post('materials/upload', 'MaterialController@upload');        Route::delete('materials/batch', 'MaterialController@batchDestroy');        //链接管理        Route::get('links', 'LinkController@index');        Route::post('links', 'LinkController@store');        Route::put('links/{id}', 'LinkController@store');        Route::delete('links/batch', 'LinkController@batchDestroy');        //区域管理        Route::get('districts', 'DistrcitController@index');        Route::get('districts/{id}', 'DistrcitController@show');        Route::post('districts', 'DistrcitController@store');        Route::put('districts/{id}', 'DistrcitController@store');        Route::delete('districts/batch', 'DistrcitController@batchDestroy');        //内容片段        Route::get('snippets', 'SnippetController@index');        Route::get('snippets/{id}', 'SnippetController@show');        Route::post('snippets', 'SnippetController@store');        Route::put('snippets/{id}', 'SnippetController@store');        Route::delete('snippets/batch', 'SnippetController@batchDestroy');        //快递管理        Route::get('expresses', 'ExpressController@index');        Route::post('expresses', 'ExpressController@store');        Route::put('expresses/{id}', 'ExpressController@store');        Route::delete('expresses/batch', 'ExpressController@batchDestroy');        //客服        Route::get('kefus', 'KefuController@index');        Route::post('kefus', 'KefuController@store');        Route::put('kefus/{id}', 'KefuController@store');        Route::delete('kefus/batch', 'KefuController@batchDestroy');        