rials', 'MaterialController@materials');        Route::get('material/image', 'MaterialController@image');        Route::post('material/delete', 'MaterialController@delete');    });    Route::namespace('Product')->group(function () {        //商品管理        Route::get('products', 'ProductController@index');        Route::get('products/{id}', 'ProductController@show');        Route::post('products', 'ProductController@store');        Route::put('products/{id}', 'ProductController@store');        Route::delete('products/batch', 'ProductController@batchDelete');        Route::post('products/batch', 'ProductController@batchUpdate');        //商品属性        Route::get('products/attributes', 'AttributeController@index');        Route::get('products/attributes/{id}', 'AttributeController@show');        Route::post('products/attributes', 'AttributeController@store');        Route::put('products/attributes/{id}', 'AttributeController@store');        Route::delete('products/attributes/batch', 'AttributeController@batchDestroy');        //产品变量        Route::get('products/variations', 'VariationController@index');        Route::get('products/variations/{id}', 'VariationController@show');        Route::post('products/variations', 'VariationController@store');        Route::put('products/variations/{id}', 'VariationController@store');        Route::delete('products/variations/batch', 'VariationController@batchDestroy');    });    Route::namespace('Shop')->group(function () {        //店铺管理        Route::get('shops', 'ShopController@index');        Route::get('shops/{id}', 'ShopController@show');        Route::post('shops', 'ShopController@store');        Route::put('shops/{id}', 'ShopController@store');        Route::delete('shops/{id}', 'ShopController@destroy');        Route::post('shops/{id}/verify', 'ShopController@verify');        Route::put('shops/batch', 'ShopController@batchUpdate');    });});