<?php/*|--------------------------------------------------------------------------| API Routes|--------------------------------------------------------------------------|| Here is where you can register API routes for your application. These| routes are loaded by the RouteServiceProvider within a group which| is assigned the "api" middleware group. Enjoy building your API!|*///use Illuminate\Support\Facades\Route;Route::group(['namespace' => 'Api'], function () {    //认证    Route::group(['namespace' => 'Auth', 'prefix' => 'auth'], function () {        Route::post('register', 'RegisterController@register');        Route::post('numberauth', 'NumberController@index');        Route::post('login-with-apple', 'AppleController@index');    });    //通用部分    Route::namespace('Common')->group(function () {        //素材管理        Route::get('materials', 'MaterialController@index');        Route::get('materials/{id}', 'MaterialController@show');        Route::put('materials/{id}', 'MaterialController@update');        Route::post('materials/upload', 'MaterialController@upload');        //页面        Route::get('pages', 'PageController@index');        Route::get('pages/{id}', 'PageController@show');        //分类        Route::get('categories', 'CategoryController@index');        Route::get('categories/{id}', 'CategoryController@show');        //链接管理        Route::get('links', 'LinkController@index');        Route::post('links', 'LinkController@store');        //区域管理        Route::get('districts', 'DistrcitController@index');        Route::get('districts/{id}', 'DistrcitController@show');        //自定义标签        Route::get('labels', 'LabelController@index');        Route::get('labels/{id}', 'LabelController@show');        //快递管理        Route::get('expresses', 'ExpressController@index');        Route::get('expresses/{id}', 'ExpressController@show');        //客服        Route::get('kefus', 'KefuController@index');        //广告管理        Route::get('ads', 'AdController@index');        Route::get('ads/{id}', 'AdController@show');        //模块管理        Route::get('blocks', 'BlockController@index');        Route::get('blocks/{id}', 'BlockController@show');        //菜单管理        Route::get('menus', 'MenuController@index');        Route::get('menus/{id}', 'MenuController@show');        //运费模板        Route::get('freight-templates', 'FreightTemplateController@index');        Route::get('freight-templates/{id}', 'FreightTemplateController@show');        //优惠券        Route::get('coupons', 'CouponController@index');        Route::post('coupons', 'CouponController@store');        //快捷内容        Route::get('shortcuts', 'ShortcutController@index');        Route::get('snippets', 'SnippetController@index');        Route::get('snippets/{id}', 'SnippetController@show');        //apns        Route::post('/apns/jpush', 'ApnsController@jpush');    });    //用户管理    Route::namespace('User')->middleware('auth:api')->group(function () {        Route::get('users', 'UserController@index');        Route::get('users/{id}', 'UserController@show');        Route::get('user/info', 'UserController@info');        Route::get('user-groups', 'GroupController@index');        //地址        Route::get('user/addresses', 'AddressController@index');        Route::get('user/addresses/{id}', 'AddressController@show');        Route::post('user/addresses', 'AddressController@store');        Route::put('user/addresses/{id}', 'AddressController@store');        Route::delete('user/addresses/{id}', 'AddressController@destroy');        //通知        Route::get('notifications', 'NotificationController@index');        Route::get('notifications/{id}', 'NotificationController@show');        Route::delete('notifications/{id}', 'NotificationController@destroy');    });    //文章管理    Route::namespace('Post')->group(function () {        Route::get('/posts', 'PostController@index');        Route::get('/posts/{id}', 'PostController@show')->where('id', '[0-9]+');        Route::get('posts/collects', 'CollectController@index')->middleware('auth:api');        Route::post('posts/collects/query', 'CollectController@query')->middleware('auth:api');        Route::post('posts/collects/toggle', 'CollectController@toggle')->middleware('auth:api');        Route::post('posts/collects/remove', 'CollectController@remove')->middleware('auth:api');    });    Route::group(['namespace' => 'Lbs', 'prefix' => 'lbs'], function () {        Route::get('geo', 'GeoCodeController@geo');        Route::get('regeo', 'GeoCodeController@regeo');    });    Route::namespace('RealEstate')->group(function () {        Route::get('properties', 'PropertyController@index');        Route::get('properties/{id}', 'PropertyController@show');        Route::post('properties/{id}/message', 'PropertyController@message');        Route::get('property-types', 'PropertyTypeController@index');        Route::get('property-features', 'PropertyFeatureController@index');        Route::get('building-types', 'BuildingTypeController@index');        Route::get('region/cities', 'RegionCityController@index');    });    Route::namespace('Buildium')->prefix('buildium')->group(function () {        Route::get('listings', 'ListingController@index');        Route::get('listings/{id}', 'ListingController@show');        Route::any('listings/{id}/message', 'ListingController@message');        Route::get('forms/{formId}/entities', 'FormController@entities');        Route::post('forms/{formId}/upload/{unitId}', 'FormController@upload');    });});